import React, { useState ,useEffect} from "react";
import app from "../firebase.config";
import { getAuth, signOut } from "firebase/auth";
import {getFirestore,doc,getDocs} from 'firebase/firestore';

import Modal from "./Modal";
import QrButon from "./QrButon";
import DeleteButon from "./DeleteButon";
import EditButon from "./EditButon";
const auth = getAuth(app);
const firestore = getFirestore(app);

const Main = () => {
    const [modal, setmodal] = useState(false);
    const [Documentos, setDocumentos] = useState(null);
    async function obtenerDocumentos() {
        const docuRef = doc(firestore,'/Documento/');
        const consulta= await getDocs(docuRef);
        if(consulta.exists()){
        return consulta.data();
        }else{

        }
    }    
    const subir=(e)=>{
        e.preventDefault();
        console.log(e.target.document.value)
    }

    useEffect(() => {
        async function feactDocumentos(){
          const datos=obtenerDocumentos();
          setDocumentos(datos)
          console.log(datos);
        }
        feactDocumentos();

    }, []);
    console.log(Documentos)
  return (
    <div className="" >
      <div className=" sm:w-auto flex flex-row justify-between px-10 bg-blue-primary p-3 text-xl uppercase">
        <p>logo</p>
        <button
          className="uppercase text-green-primary  hover:text-white "
          onClick={() => signOut(auth)}
        >
          cerrar sesion
        </button>
      </div>
      { modal &&
      <Modal titulo={"Subir Documento"} cerrar={()=>setmodal(!modal)}>
        <div className="p-8">
            <form onSubmit={subir} className="flex flex-col">
                <label htmlFor="Titulo">Titulo:</label>
                <input className="p-2 border-b border-blue-primary mb-2" type="text" autoFocus required placeholder="Ingrese Un Titulo" id="Titulo"/>
                <label htmlFor="Descripcion">Nombre del Paciente</label>
                <input className="p-2 border-b border-blue-primary mb-2" type="text" placeholder="Ingrese Nombre del Paciente" maxLength="200" id="Descripcion" />
                <label htmlFor="document">Subir Document</label>
                <input type="file" name="pdf" id="document" multiple={false} accept=".pdf" required/>

                <input className="mx-auto mt-10 w-2/3 bg-blue-primary text-white rounded-md hover:bg-green-primary" type="submit" value="Subir"/>
            </form>
        </div>
      </Modal>
        }
      <div className="bg-gray-300 h-screen">
        <div className="flex flex-col items-stretch  ">
          <button onClick={()=>setmodal(!modal)} className="flex flex-row justify-evenly text-white rounded-md bg-blue-500 w-52 p-4 text-2xl font-bold ml-24 mb-14 mt-10">
            <p>Nuevo</p>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-8 w-8 text-white"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M12 4v16m8-8H4"
              />
            </svg>
          </button>

          <div className=" container m-auto ">
            <table className="m-auto bg-white">
              <thead className="  bg-blue-primary h-5 text-white">
                <tr>
                  <th className=" hidden sm:block ">Titulo</th>
                  <th className="w-2/12 sm:w-4/12">Nombre Del Paciente</th>
                  <th className="w-1/12 sm:w-1/12">Fecha</th>
                  <th className="w-1/12 sm:w-1/12">Editar</th>
                  <th className="w-1/12 sm:w-1/12">Eliminar</th>
                  <th className="w-1/12 sm:w-1/12">Descargar Qr</th>
                </tr>
              </thead>
              <tbody className="text-center ">
                <tr className="border-b  border-blue-primary">
                  <td className=" hidden sm:block ">Titulo prueb dsf sfa</td>
                  <td>Description</td>
                  <td>28/6/2021</td>
                  <td className="hover:bg-blue-primary hover:text-white">
                    <button><EditButon/></button>
                  </td>
                  <td className="hover:bg-blue-primary hover:text-white">
                    <button><DeleteButon/></button>
                  </td>
                  <td className="hover:bg-blue-primary hover:text-white">
                    <button ><QrButon/></button>
                  </td>
                </tr>

                <tr className="border-b  border-blue-primary">
                  <td className="hidden sm:block ">Titulo prueba</td>
                  <td >
                 Caceres Palma Guillermo Enrique   
                  </td>
                  <td>28/6/2021</td>
                  <td>
                    <button>editar</button>
                  </td>
                  <td>
                    <button>Eliminar</button>
                  </td>
                  <td>
                    <button><QrButon/></button>
                  </td>
                </tr>
                <tr className="bg-blue-primary h-5 rounded-b-3xl">
                  <td colSpan="6 mb-10"></td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Main;
