import React, { useEffect, useState } from "react";
import Select from "react-select";
import axios from 'axios'
import app  from "../firebase.config";
import { getStorage, ref, uploadBytes, getDownloadURL ,deleteObject} from "firebase/storage";
import {headers,Url as lru} from '../config';



const AddRegistro = () => {
  const storage = getStorage(app);
  const [pacientes,setPacientes]=useState([])
  const [examenes,setExamenes]=useState([])
  useEffect( () => {
    headers.Authorization='Bearer '+localStorage.getItem('Token')
    axios.get(lru+"Registro/GetAll",{headers}).then(res=>{
      setPacientes(res.data.Paciente)
      setExamenes(res.data.Examen)
    }).catch(error=>console.log(error))
  }, [])


  const [data, setData] = useState({'Examen':null,'Paciente':null,'Url':null,'Fecha':null});
  const [file, setFile] = useState(null);
  const [errorExamen,setErrorExamen] = useState(null);
  const [errorPaciente,setErrorPaciente] = useState(null);
  const [errorUrl,setErrorUrl] = useState(null);
  const [errorFecha,setErrorFecha] = useState(null);

  const handleChange = (e) => {
    e.preventDefault();
    if(data.Url){
      let archivo =data.Url.split('%2F')[1].split('?')[0].replaceAll('%20',' ')
      const desertRef = ref(storage, `documentos/${archivo}`);
      deleteObject(desertRef).then(()=>{
        data.Url=null;
        setData(data)
      }).catch(error=>{
        console.log(error)
      })
    }


    setFile(e.target.files[0]);

  };


  const addRegistro = async () =>{

    if(!data.Paciente) setErrorPaciente('el Paciente es obligatorio') 
    if(!data.Examen) setErrorPaciente('el Examen obligatorio') 


    if(file && !data.Url){

      const minuto = new Date().getMinutes();
      const seconds = new Date().getMilliseconds();
      const sec = new Date().getSeconds();
      const archivo = ref(
        storage,
        `documentos/${file.name.split('.pdf')[0]}-${seconds}-${minuto}-${sec}.pdf`
      );
      await uploadBytes(archivo, file);
      const res =await getDownloadURL(archivo);
      data.Url=res
      setData(data)
    }
  //  for (let element in data) {
  //    if(!data[element]) {message[element]=`${element} es obligatorio`; setMessage(message);window['set'+element](true)}
  //    else{message[element]=null; setMessage(message);eval('set'+element+'(false)')}
  //  }

   console.log('data',data)
   console.log(`url : ${data.Url}`)

  } ;

  const handleChangeD=(param)=>(e)=>{
    if(param!=='Fecha')data[param]=e.value
    else data[param]=e.target.value
    setData(data)
  }
  return (
    <form

      className="flex flex-col bg-white w-auto p-4 justify-between"
    >
      <label className="mt-4 mb-1" htmlFor="Paciente">
        Paciente
      </label>
      <Select
        id="Paciente"
        options={pacientes}
        onChange={handleChangeD('Paciente')}

      />
      <span className="Warring">
      {errorPaciente &&errorPaciente}
      </span>

      <label className="mt-4 mb-1" htmlFor="Examen">
        Examen
      </label>
      <Select
        id="Examen"
        className="w-96"
        options={examenes}
        onChange={handleChangeD('Examen')}
        onBlur={()=>setErrorPaciente(null)}
      />
      <span className="Warring">
      {errorExamen &&errorExamen}
      </span>
      <label className="mt-4 mb-1" htmlFor="Archivo">
        Subir Pdf
      </label>
      <input
        onChange={handleChange}
        type="file"
        name="Archivo"
        id="Archivo"
        accept="application/pdf "
      />
      <label className="mt-4 mb-1" htmlFor="Fecha">
        Fecha
      </label>
      <input
        type="date"
        name="Fecha"
        id="Fecha"
        onChange={handleChangeD('Fecha')}
      />
      <input onClick={()=>addRegistro()} className="w-2/3 my-6 ml-16" type="button" value="Agregar" />
    </form>
  );
};

export default AddRegistro;
