import React from 'react'
import { useEffect, useState } from 'react/cjs/react.development';
import AddPaciente from '../componets/AddPaciente';
import Modal from '../componets/Modal';
import axios from 'axios';
import { headers, Url} from '../config'
import Basura from '../componets/Basura';

function Pacientes() {

    const [AcitveModal,setAcitveModal] = useState(false);
    const [data,setdata] = useState([]);
    const [refresh,setrefresh] = useState(false);
    headers.Authorization = 'Bearer ' + localStorage.getItem('Token')
    useEffect(() => {

      axios.get(Url+"Paciente/Gets",{headers}).then(res=>{
        if(res.data.ok){setdata(res.data.data)}

      }).catch(error=>console.log(error))

    }, [refresh])

    async function eliminar(id) {
      try {
        const res= await axios.delete(Url+`Paciente/Delete/${id}`,{headers}) 
        if(res.data.ok) setrefresh(!refresh)
        else console.log(res.data)
      } catch (error) {
        console.log(error)
      }
    }

    return (
      <div className="container  min-h-screen">
        {AcitveModal&&<Modal cerrar={()=>setAcitveModal(false) } titulo={'Agregar Paciente'}><AddPaciente salir={()=>setAcitveModal(!Modal)} reload={()=>setrefresh(!refresh)}/></Modal>}
        <button onClick={()=>setAcitveModal(true)} className="m-4 bg-green-primary py-2 px-4 rounded-lg text-white text-lg"> + Agregar Paciente</button>

        <table className="bg-white w-8/12 mx-auto mt-6 rounded-t-lg">
          <thead className="bg-blue-primary text-white rounded-t-lg">
            <tr className="text-xl rounded-t-lg">
              <th className="rounded-tl-lg">Nº Cedula</th>
              <th className="">Nombre</th>
              <th className='rounded-tr-lg w-10'></th>
            </tr>

          </thead>
          <tbody className="text-center">
            {data.map((element,i) => {
              let color ="bg-gray-200"
              if(i%2===0) color ="bg-white"
              return (
                  <tr className={`${color} `}>
                    <td className='py-2'>{element.Cedula}</td>
                    <td className='py-2'>{element.Nombre}</td>
                    <th className='py-2 bg-red-500 ml-2 border border-red-500 cursor-pointer  hover:bg-white hover:text-red-500   '><Basura/></th>
                  </tr>
                );
            })}
          </tbody>

        </table>
      </div>
  )
}

export default Pacientes
