import React from 'react';
import app from '../firebase.config';
import {getAuth,signInWithEmailAndPassword} from 'firebase/auth';
const auth=getAuth(app);
const Login = () => {
    const handelsubmit =(e)=>{
        e.preventDefault();
        const email=e.target.correo.value;
        const password=e.target.password.value;
        signInWithEmailAndPassword(auth,email,password)
        console.log(e.target.correo.value);
        console.log(e.target.password.value)
    }
    return (
        <div className= "flex flex-1 h-screen bg-gradient-to-t from-green-primary  to-white">
            <div className=" flex flex-col bg-blue-primary w-2/6 m-auto rounded-3xl shadow-2xl">
                <h1 className="text-center text-4xl font-extrabold p-6 text-green-primary">Iniciar Session</h1>

                <form  onSubmit={handelsubmit} className="flex flex-col p-5 pb-10 justify-between" >
                    <label className="text-green-primary font-semibold text-3xl" htmlFor="correo">Correo:</label>
                    <input className="m-4 my-2  bg-transparent border-b border-green-primary focus:outline-white text-white"  type="email" id="correo" placeholder="Ingrese su Correo" autoFocus required/>
                    <label className="mt-6 text-green-primary font-semibold text-3xl" htmlFor="password">Contraseña:</label>
                    <input className="m-4 my-2 bg-transparent border-b border-green-primary focus:outline-white text-white"  type="password" id="password" placeholder="Ingrese su Contraseña" required/>
                    <input className="mt-16 w-1/2 m-auto rounded-3xl h-14 border bg-green-primary text-3xl font-bold uppercase text-white  hover:bg-white hover:text-green-primary hover:border-green-primary focus:bg-gray-400" type="submit" value="Ingresar"/>
                </form>
            </div>
        </div>
    )
}

export default Login
