import React, { useState } from "react";
import Select from "react-select";
import { useForm } from "react-hook-form";

const options = [
  { value: "1316168077", label: "juan" },
  { value: "1316166977", label: "Melanie" },
  { value: "1316168577", label: "Vanilla" },
];

const AddRegistro = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();
  const [data, setData] = useState({'Examen':null,'Paciente':null,'Url':null,'Fecha':null});
  const [file, setFile] = useState(null);
  const [enviar,setEnviar] = useState(false);
  const [message,setMessage] = useState({'Examen':null,'Paciente':null,'Url':null,'Fecha':null});
  const handleChange = (e) => {
    e.preventDefault();
    setFile(e.target.files[0]);

  };
  const addRegistro = () =>{
   for (let element in message) {
     if(!element) {message[element]=`El ${element} es obligatorio`}

   }
  } ;
  const handleChangeD=(param)=>(e)=>{
    if(param!=='Fecha')data[param]=e.value
    else data[param]=e.target.value
    setData(data)
  }
  return (
    <form

      className="flex flex-col bg-white w-auto p-4 justify-between"
    >
      <label className="mt-4 mb-1" htmlFor="Paciente">
        Paciente
      </label>
      <Select
        id="Paciente"
        options={options}
        onChange={handleChangeD('Paciente')}

      />
      <span className="Warring">
        {message.Paciente && message.Paciente}
      </span>
      <label className="mt-4 mb-1" htmlFor="Examen">
        Examen
      </label>
      <Select
        id="Examen"
        className="w-96"
        options={options}
        onChange={handleChangeD('Examen')}
      />
      <span className="Warring">{errors.Examen && errors.Examen.message}</span>
      <label className="mt-4 mb-1" htmlFor="Archivo">
        Subir Pdf
      </label>
      <input
        onChange={handleChange}
        type="file"
        name="Archivo"
        id="Archivo"
        accept="application/pdf "
      />
      <label className="mt-4 mb-1" htmlFor="Fecha">
        Fecha
      </label>
      <input
        type="date"
        name="Fecha"
        id="Fecha"
        onChange={handleChangeD('Fecha')}
      />
      <span className="Warring">{errors.Fecha && errors.Fecha.message}</span>
      <input onClick={()=>addRegistro()} className="w-2/3 my-6 ml-16" type="button" value="Agregar" />
    </form>
  );
};

export default AddRegistro;
