import React, { useState, useEffect } from "react";
import app, { db } from "../firebase.config";
import { getAuth, signOut } from "firebase/auth";
import {
  collection,
  getDocs,
  query,
  doc,
  addDoc,
  deleteDoc,
  updateDoc,
} from "firebase/firestore";
import { getStorage, ref, uploadBytes, getDownloadURL } from "firebase/storage";
import QRImage from "react-qr-image";
import Modal from "./Modal";
import QrButon from "./QrButon";
import DeleteButon from "./DeleteButon";
import logo from '../assets/img/logo.png';

const auth = getAuth(app);
const storage = getStorage(app);
const Main = () => {
  const [modal, setmodal] = useState(false);
  const [modalQr, setmodalQr] = useState(null);
  const [ModalUpdate, setmodalUpdate] = useState(false);
  const [Documentos, setDocumentos] = useState([]);
  const [rerender, setrerender] = useState(false);
  const [urlDescarga, seturlDescarga] = useState(null);
  const [message, setmessage] = useState(null);
  async function obtenerDocumentos() {
    const result = await getDocs(query(collection(db, "Documento")));
    return result;
  }
  const subir = async (e) => {
    e.preventDefault();
    console.log("url: " + urlDescarga);
    if (urlDescarga) {
      const titulo = e.target.Titulo.value;
      const pacinete = e.target.Paciente.value;
      let today = new Date();
      let dd = today.getDate();
      let mm = today.getMonth() + 1; //January is 0!
      let yyyy = today.getFullYear();

      if (dd < 10) {
        dd = "0" + dd;
      }

      if (mm < 10) {
        mm = "0" + mm;
      }
      today = dd + "/" + mm + "/" + yyyy;
      const jsondata = {
        Titulo: titulo,
        Paciente: pacinete,
        fecha: today,
        url: urlDescarga,
      };
      addDoc(collection(db, "Documento"), JSON.parse(JSON.stringify(jsondata)));
      setmodal(!modal);
      seturlDescarga(null);
      setmessage(null);
    } else {
      setmessage("Cargando el documento intente de nuevo");
    }
  };

  const update = (id) => async (e) => {
    e.preventDefault();
    console.log("url: " + urlDescarga);

    const titulo = e.target.Titulo.value;
    const pacinete = e.target.Paciente.value;
    let today = new Date();
    let dd = today.getDate();
    let mm = today.getMonth() + 1; //January is 0!
    let yyyy = today.getFullYear();

    if (dd < 10) {
      dd = "0" + dd;
    }

    if (mm < 10) {
      mm = "0" + mm;
    }
    today = mm + "/" + dd + "/" + yyyy;
    const jsondata = {
      Titulo: titulo,
      Paciente: pacinete,
      fecha: today,
      url: ModalUpdate.url,
    };
    // console.log(collection(db, `Documento/${id}`));
    //console.log(id)
    const documeref = doc(db, `Documento/${id}`);
    //console.log(documeref);
    updateDoc(documeref, JSON.parse(JSON.stringify(jsondata)));

    setmodalUpdate(!modal);
    seturlDescarga(null);
    setmessage(null);
  };


  const fileHandel = async (e) => {
    const pdf = e.target.files[0];
    const minuto = new Date().getMinutes();
    const seconds = new Date().getMilliseconds();
    const sec = new Date().getSeconds();
    const archivo = ref(
      storage,
      `documentos/${pdf.name}-${seconds}-${minuto}-${sec}`
    );
    await uploadBytes(archivo, pdf);
    seturlDescarga(await getDownloadURL(archivo));
  };
  function QRmodal(url) {
    setmodalQr(url);
  }
  async function eliminar(id) {
    await deleteDoc(doc(db, "Documento", id));
    setrerender(!rerender);
  }

  useEffect(() => {
    const Obtenerdatos = async () => {
      const data = await obtenerDocumentos();

      setDocumentos(data.docs);
    };
    Obtenerdatos();
