import React, { useEffect, useState } from "react";
import Select from "react-select";
import axios from 'axios'
import app  from "../firebase.config";
import { getStorage, ref, uploadBytes, getDownloadURL } from "firebase/storage";
import {headers,Url as lru} from '../config';

const AddRegistro = () => {
  const storage = getStorage(app);
  const [pacientes,setPacientes]=useState([])
  const [examenes,setExamenes]=useState([])
  useEffect( () => {
    headers.Authorization='Bearer '+localStorage.getItem('Token')
    axios.get(lru+"Registro/GetAll",{headers}).then(res=>{
      setPacientes(res.data.Paciente)
      setExamenes(res.data.Examen)
      console.log(pacientes,examenes)

    }).catch(error=>console.log(error))
  }, [])


  const [data, setData] = useState({'Examen':null,'Paciente':null,'Url':null,'Fecha':null});
  const [file, setFile] = useState(null);
  const [Examen,setExamen] = useState(false);
  const [Paciente,setPaciente] = useState(false);
  const [Url,setUrl] = useState(false);
  const [Fecha,setFecha] = useState(false);
  const [message,setMessage] = useState({'Examen':null,'Paciente':null,'Url':null,'Fecha':null});
  const handleChange = (e) => {
    e.preventDefault();
    setFile(e.target.files[0]);

  };
  const addRegistro = () =>{
    data.Url='asdas'; 
  //  for (let element in data) {
  //    if(!data[element]) {message[element]=`${element} es obligatorio`; setMessage(message);window['set'+element](true)}
  //    else{message[element]=null; setMessage(message);eval('set'+element+'(false)')}
  //  }

   console.log('data',data)
   console.log('message',message)
  } ;

  const handleChangeD=(param)=>(e)=>{
    if(param!=='Fecha')data[param]=e.value
    else data[param]=e.target.value
    setData(data)
  }
  return (
    <form

      className="flex flex-col bg-white w-auto p-4 justify-between"
    >
      <label className="mt-4 mb-1" htmlFor="Paciente">
        Paciente
      </label>
      <Select
        id="Paciente"
        options={pacientes}
        onChange={handleChangeD('Paciente')}

      />
      <span className="Warring">
      {Paciente &&message.Paciente}
      </span>

      <label className="mt-4 mb-1" htmlFor="Examen">
        Examen
      </label>
      <Select
        id="Examen"
        className="w-96"
        options={examenes}
        onChange={handleChangeD('Examen')}
      />

      <label className="mt-4 mb-1" htmlFor="Archivo">
        Subir Pdf
      </label>
      <input
        onChange={handleChange}
        type="file"
        name="Archivo"
        id="Archivo"
        accept="application/pdf "
      />
      <label className="mt-4 mb-1" htmlFor="Fecha">
        Fecha
      </label>
      <input
        type="date"
        name="Fecha"
        id="Fecha"
        onChange={handleChangeD('Fecha')}
      />
      <input onClick={()=>addRegistro()} className="w-2/3 my-6 ml-16" type="button" value="Agregar" />
    </form>
  );
};

export default AddRegistro;
