import React from 'react'
import {useForm} from 'react-hook-form'
const AddExamen = () => {
  const {register,handleSubmit,formState:{errors}} = useForm()
  const addExamen=(data)=>{

    console.log(data)
  }
  return (
    <form onSubmit={handleSubmit(addExamen)} className='flex flex-col bg-white w-auto p-4 justify-between'>
        <label className='mt-4 mb-1' htmlFor="Nombre">Examen</label>
        <input className='w-96 border-b border-gray-600' type="text" name="Nombre" id="Nombre" minLength={10} maxLength={50} placeholder='Nombre de Examen'
        {...register('Nombre',{
          required:{value:true,message:'Nombre de examen es obligatorio'},
          maxLength:{value:50,message:'Nombre no debe tener mas 50 caracteres'}
        })}
        />
         <span className='Warring'>{errors.Nombre && errors.Nombre.message }</span>
        <label className='mt-4 mb-1' htmlFor="Nombre">Descripcion</label>
        <textarea className='w-96 border-b p-2'  minLength={1} maxLength={200} placeholder='Breve Descripcion' /> 

        <input className='m-auto w-2/3 my-6' type="submit" value="Agregar" />
    </form>
  )
}

export default AddExamen
